import React, { Component } from "react";
import "./index.css";
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Button from "react-bootstrap/Button";
import { NavLink, Switch, Route } from "react-router-dom";
import Scanner from "./Scanner";
import Result from "./Result";
import {db, auth } from './firebase'

const App = () => (
  <div className="app">
    <Navigation />
    <Main />
  </div>
);

const Navigation = () => (
  <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
    <Navbar.Toggle aria-controls="responsive-navbar-nav" />
    <Navbar.Collapse id="responsive-navbar-nav">
      <Nav className="mr-auto">
        <ul>
          <li>
            <NavLink exact activeClassName="current" to="/">
              Home
            </NavLink>
          </li>
          <li>
            <NavLink exact activeClassName="current" to="/Cart">
              Cart
            </NavLink>
          </li>
        </ul>
      </Nav>
    </Navbar.Collapse>
  </Navbar>
);

const Home = () => (
  <div className="home">
    <h1>Objective</h1>
    <p>
      {" "}
      HOORAY! Your favourite Uncle Charles and his family have recently returned
      from USA and want to have dinner with you.
    </p>
    <p>
      Treat them to a healthy and delicious meal prepared by you. Its time to
      head to the mini mart and purchase the ingredients you need to cook a meal
      for a family of four.
    </p>

    <Button
      id="btnLG" variant="outline-light"
      onClick={() => (window.location.href = "/Cart")}
    >
      {" "}
      Let's GO!{" "}
    </Button>
  </div>
);

