    };

    _onDetected = (result) => {
      this.setState({ results: this.state.results.concat([result]) });
    };

    componentDidMount(){
      console.log('mounted')
      db.collection('data')
        .get()
        .then (snapshot => {
          const data = []
          snapshot.forEach( doc => {
            const dataset = doc.dataset()
            data.push(dataset)
          })
          this.setState({ data: data})

          //console.log(snapshot)
        })
        .catch (error => console.log(error))
    }

    render() {
      return (
        <div>
          <Button id="scanbtn"variant="outline-light" onClick={this._scan}>
            {this.state.scanning ? "Stop Scan Barcode" : "Start Scan Barcode"}
          </Button>
          <ul className="results">
            {
