import React, { Component } from "react";
import "./index.css";
import Button from "react-bootstrap/Button";
import Navigation from './components/Navigation/Navigation'
import Home from './components/Home/Home'
import History from './c'


import { BrowserRouter, Switch, Route } from "react-router-dom";
import Scanner from "./components/Scanner/Scanner";


class App extends Component {

render(){
let routes = (
  <Switch>
    <Route exact path="/" component={Home}></Route>
    <Route exact path="/Cart" component={History}></Route>
  </Switch>
)
  return (
    <BrowserRouter>
  <div className="bkg">
        <Navigation />
    <div className="container">

    {routes}
    </div>
  </div>
  </BrowserRouter>
)};
}


  class Cart extends Component {
    state = {
      scanning: false,
      results: [],
    };

    _scan = () => {
      this.setState({ scanning: !this.state.scanning });
    };

    _onDetected = (result) => {
      this.setState({ results: this.state.results.concat([result]) });
    };


    render() {
      return (
        <div>
          <Button id="scanbtn"variant="outline-light" onClick={this._scan}>
            {this.state.scanning ? "Stop Scan Barcode" : "Start Scan Barcode"}
          </Button>
          <ul className="results">

            {this.state.results.map((result, i) => (
              <Result key={result.codeResult.code + i} result={result} />
            ))}
          </ul>
          {this.state.scanning ? (
            <Scanner onDetected={this._onDetected} />
          ) : null}
        </div>
      );
    }
  };



export default App;
