import React from 'react';
import './index.css';
import Navbar from "react-bootstrap/Navbar";
import Nav from "react-bootstrap/Nav";
import Button from "react-bootstrap/Button";
import { NavLink, Switch, Route } from 'react-router-dom';
import Scanner from "./Scanner";
import Result from "./Result";



const App = () => (
  <div className='app'>
    <Navigation />
    <Main />
  </div>
);

const Navigation = () => (
  <Navbar collapseOnSelect expand="lg" bg="dark" variant="dark">
            <Navbar.Toggle aria-controls="responsive-navbar-nav" />
            <Navbar.Collapse id="responsive-navbar-nav">
              <Nav className="mr-auto">
                <ul>
                <li><NavLink exact activeClassName="current" to="/">Home</NavLink></li>
                <li><NavLink exact activeClassName="current" to="/Cart">Cart</NavLink></li>
                <li><NavLink exact activeClassName="current" to="/Scan">Scan</NavLink></li>
                </ul>
              </Nav>
            </Navbar.Collapse>
          </Navbar>
  );

  const Home = () => (
    <div className='home'>
      <h1>Objective</h1>
      <p> HOORAY! Your favourite Uncle Charles and his family have recently returned from USA and want to have dinner with you.</p> 
      <p>Treat them to a healthy and delicious meal prepared by you. Its time to head to the mini mart and purchase the ingredients you need to cook a meal for a family of four.</p>

    <Button variant="outline-light" onClick={() => window.location.href='/Cart'}> Let's GO! </Button>
    </div>
  );

  const Cart = () => (
    <div className='cart'>
      <h1>About Me</h1>
      <p>Ipsum dolor dolorem consectetur est velit fugiat. Dolorem provident corporis fuga saepe distinctio ipsam? Et quos harum excepturi dolorum molestias?</p>
      <p>Ipsum dolor dolorem consectetur est velit fugiat. Dolorem provident corporis fuga saepe distinctio ipsam? Et quos harum excepturi dolorum molestias?</p>
    </div>
  );

  const Scan = () => (
      class App extends Component {
    state = {
      scanning: false,
      results: [],
    };

    _scan = () => {
      this.setState({ scanning: !this.state.scanning });
    };

    _onDetected = (result) => {
      this.setState({ results: this.state.results.concat([result]) });
    };

    render() {
      return (
        <div>
          <Button variant="primary" onClick={this._scan}>
            {this.state.scanning ? "Stop Camera" : "Start Camera"}
          </Button>
          <ul className="results">
            {this.state.results.map((result, i) => (
              <Result key={result.codeResult.code + i} result={result} />
            ))}
          </ul>
          {this.state.scanning ? <Scanner onDetected={this._onDetected} /> : null}
        </div>
      );
    }
  }
  );

  const Main = () => (
    <Switch>
      <Route exact path='/' component={Home}></Route>
      <Route exact path='/Cart' component={Cart}></Route>
      <Route exact path='/Scan' component={Scan}></Route>
    </Switch>
  );

export default App;
