import React, { Component } from 'react';
import Scanner from './Scanner';
import Result from './Result';
import Button from 'react-bootstrap/Button';
import Navbar from 'react-bootstrap/Navbar';
import Nav from 'react-bootstrap/Nav';

class App extends Component {

  state = {
    scanning: false,
    results: []
  }

  _scan = () => {
    this.setState({scanning: !this.state.scanning});
  }

  _onDetected = (result) => {
    this.setState({results: this.state.results.concat([result])});
  }

  render() {
    return (
      <div>
        <Navbar justify collapseOnSelect expand="lg" bg="dark" variant="dark">
  <Navbar.Brand href="#home">React-Bootstrap</Navbar.Brand>
  <Navbar.Toggle aria-controls="responsive-navbar-nav" />
  <Navbar.Collapse id="responsive-navbar-nav">
    <Nav justify className="mr-auto">
      <Nav.Link href="#features">Home</Nav.Link>
      <Nav.Link href="#pricing">Cart</Nav.Link>
      <Nav.Link href="#pricing">Scan</Nav.Link>
    </Nav>
  </Navbar.Collapse>
</Navbar>
          <Button variant="primary" onClick={this._scan}>{this.state.scanning ? 'Stop Camera' : 'Start Camera'}</Button>
          <ul className="results">
            {this.state.results.map((result, i) => (<Result key={result.codeResult.code + i} result={result} />))}
          </ul>
          {this.state.scanning ? <Scanner onDetected={this._onDetected}/> : null}
      </div>
    )
  }

}

export default App;
